INSERT INTO `fu_curlture` VALUES ('1', '太上慈悲九幽拔罪忏卷之一', '<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">序洞玄部威仪类</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">太上慈悲九幽拔罪忏</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">经名：太上慈悲九幽拔罪忏。十卷。底本出处：《正统道藏》洞玄部威仪类。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">太上慈悲九幽拔罪忏卷之一</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">尔时元始天尊，时游十方世界，天上地下无所不经，至大福堂国，见虚皇道君，为十方国土、一切众生、善男女等，心生厌故，遂即分身应变，口吐五色琼光，遍照十方无不明彻。尔时男女及诸四众，昼夜惶惧，悲声叹泣，瞻仰尊颜，稽首顶礼，叩头博颡，肘行膝步。诸禽杂兽，并皆如是。一切众生，诸方仰视，寂绝形影，莫知所寻。尔时，虚皇道君，慈愍众生，分身下界，安坐碧霞神宝之座，念彼众生如常说法，演教化人。一切含灵，及诸禽兽，皆来诣座，稽首作礼，步步而前。重遇道君，欢喜踧踖。是时，座下有一真人，名曰普救，从众中出，稽首作礼，上白天尊，愿闻演说。十方男女，生可获福，死得受生，作何因缘，得遂本愿。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">天尊告曰，汝可复坐，静念在心，一切因缘，吾为解说。天尊言，十方世界，皆有天堂。若有男女，常礼十方应号天尊，当生可获福，死得受生。於是普救真人，及诸四辈，长跪齐心，而听演说。尔时，天尊先说，东方青灵始老苍帝、九炁天君，华林境界，其有真人，名曰静境无极之国、惠静庄严。若人临终之日，愿生彼国，当礼东方应号天尊，长跪忏悔，免恒沙罪障，获无量善，因炼质澄神，登真悟道。众等至心，今当敬礼。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">人各恭敬</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，慈悲三宝天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，寻声救苦天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，东方玉宝皇上天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，普慈护国天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，大忍无碍天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，东华上相天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，清宫演化天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，仙灵妙宝天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，含和虚极天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，九天积善天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，皇上真老天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，元灵隐曜天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，金阙玉童天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，凝心玄应天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，灵都大度天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，玄灵至德天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，高上太虚天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，圆通妙极天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，开明真相天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，宝胜元和天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，宝藏圆极天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，玄明真定天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，众智成满天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，金华上相天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，真行明因天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，威光宝际天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，开通信悟天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，慧解明智天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，紫虚上极天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，虚心普照天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，惠源广润天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，正觉垂训天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，真智照极天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，仙度妙宝天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，紫阳灵应天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，妙意无边天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，金光善惠天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，玄虚真行天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，惠空度脱天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，庄严玄觉天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，灵感遍通天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，虚皇降照天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，妙极真源天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，玄应惠明天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，宝相真阳天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，凝神湛照天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，法云普润天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，虚明远览天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，法相圆满天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，妙意端严天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，本际玄微天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，冲真寂照天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">礼三宝已，复次忏悔</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心归命，十方常住三宝。臣等忏悔累劫以来过误之罪。众等暗昧，不省前非，或造作恶缘，施为信意。心如火盛，炎炎而不拣媸妍；意似刀锋，凛凛而不论强弱。积时累日，经月逾年，未遇首陈，虑遭涂炭，冰池火柱，剑树镬汤，寒即痛心，热来焦体，断筋伤骨，腐腹糜肠。如此罪元，乞垂涤荡。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">天尊言，若有善男子、善女人，未终之者，虽然造罪，亦复修福。在生修福，十分得八；将福补罪，多少始知。若当过去造诸功德，七分之中，惟获其一，计功补过，福乃有余，如此比量，始名得一。若将善功，补罪不足，即当涉历三涂，受诸苦恼。一日一夜，万死万生，备受诸苦。若有亡人，临终之日，诸方请师，及见在男女、香火知识同於一处，烧香忏悔，受戒诵经，先取亡人身上衣物，将对香火，忏悔受戒。从身亡之后，三七日内，别置清净之所，安立道场，燃灯续明，赞咏行道，鸣锺礼拜，虔恳投诚。所以尔者，急修功德。初亡之人，方开道路，得悟东西，乘此善缘，往生极乐。先亡获救，见在蒙恩。忏悔既终，志心敬礼。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">人各恭敬</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，妙乐威光天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，普照幽冥天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，法缘无尽天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，随机赴感天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，正智玄虚天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，惠日流光天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，琼华炳耀天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，浮香远览天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，飞云广覆天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，普度药王天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，海空惠解天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，慈悲兴乐天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，虚皇普济天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，法灯朗照天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，高上太虚天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，大慈救苦天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，安住至乐天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，玄和妙宝天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，无为定惠天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，玄虚严静天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，惠量应物夭尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，庄严素德天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，虚玄至极天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，妙奉自然天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，慈悲救度天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，无名宝相天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，法云垂荫天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，慈恩广化天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，玄津普润天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，云和积善天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，大惠凝湛天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，广开戒定天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，嘱累法宝天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，冥神洞照天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，崇仙开化天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，大弘正法天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，无边普度天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，法桥广济天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，德藏包含天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，权变应现天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，威德大慈天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，炎光灵耀天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，丹元光妙天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，朱明法本天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，慧命凝常天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，丹灵定慧天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，威光度脱天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，金阙化身天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，逍遥快乐天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，火炼丹界天尊。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，大慈大悲拔难真人。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，大慈大悲定惠真人。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，大慈大悲济苦真人。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心朝礼，大慈大悲上生真人。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">礼三宝已，复次忏悔</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">志心归命，十方常住三宝天尊。忏悔累劫以来，过误之罪。众等托荫父母胞胎，或富或贫，或贵或贱，或善或恶，或女或男，莫省轮回，难分终始。前虽忏悔，虑有不周。一切冤愆，咸希洗雪。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">天尊言，若有善男子、善女人，欲使临终之日，愿生十方妙乐境界者，当以洁净身心，洗除秽垢，严整衣服，舍己资财，烧众妙香，请师礼忏，悬诸旛盖，音声赞咏，供养三宝，口诵尊经，一切庄严，善依言说，真法智惠，不可思议。计斯众生，观此肉身，从空至空，寂灭假合，地水火风。若有休聚败坏，精进行道，可得拔度，随意止住，福果必登，真心具备，出离地狱，受乐自然，得见大道。尔时，元始天尊告太上道君曰，卿与普救真人，游行十方，见有汝等弟子，愿生於十方快乐之国，无极无穷，不可称计。于时天尊道君、诸天圣众、所有清静之境，亦无极量，不可称说。我於大众中，为诸四辈未显之界一切众生，晓示妙境内外三界，诸如国土飞天真人、得道圣众，所有名讳，略示缘由。若能精心奉礼，功满三千，布施众生，勤行无怠，当生净境，不滞幽牢，悉愿归依，稽首作礼而退。</span>\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<span style=\"font-size:18px;\">太上慈悲九幽拔罪忏卷之一竟</span>\r\n</p>', '1448325823', '0', '', '', '', '');
INSERT INTO `fu_curlture` VALUES ('2', '太上慈悲九幽拔罪忏卷之二', '<p style=\"font-family:宋体;\">\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">太上慈悲九幽拔罪忏卷之二</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">尔时，元始天尊为普救真人，演说东方青灵始老苍帝、九炁天君，华林境界、无极之国，惠静庄严。既竟重为演说，东南方梵炁天君，碧霞境界，其有真人名曰，真安无极之国，妙寂庄严。若人临终之日，愿生彼国，当洁己清斋，请师奉礼，东南方应号天尊，长跪忏悔，免恒沙罪障，获无量善因。炼质澄神，登真悟道，众等至心，今当敬礼。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">人各恭敬</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，东南方好生度命天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，慈风广扇天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，法身出世天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，无碍自在天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，巧说法相天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，增益惠解天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，善权方便天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，至寂澄明天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，智惠无等天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，太微应号天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，通玄定相天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，洞鉴幽明天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，梵明弘教天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，安乐胜报天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，升玄至极天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，妙光宝胜天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，中和大惠天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，一乘虚极天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄宗诞应天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，无量威化天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，宣辉妙觉天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，冥神洞宴天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，金映护魂天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，西灵惠照天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，四极显真天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄真九老天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，上圣十华天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，四虚普照天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，真元七照天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，二素皓灵天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，金门演教天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，太华上极天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，空洞虚皇天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄光度世天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，九空太素天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，灵耀八景天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，七觉照疑天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，三素光明天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大洞景华天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，无上真如天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，元和宝胜天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，定光命禄天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，皓华素德天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，妙宝净光天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，真明虚极天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，冲和灵应天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，太和高上天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，虚极妙灵天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，宝华元梵天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，净藏秘景天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">礼三宝已，复次忏悔</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">至心归命，十方常住三宝。臣等忏悔身业之罪，自从生诞，形体分张，首负圆天，足履方地，肌尸秽丑，骨血腥凡，不蔽星辰，恒干风雨，累此阳过，必犯阴愆。凭此善缘，咸希忏洗。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">天尊言曰，若有善男子、善女人，为其亡人，初终百日、周年远忌，造诸经像斋供，财货虽多，获功弥少。但缘心但慢易，意且粗疏，残食献斋，余财设像。一舍一悭，或作或休，名价虽高，计功全寡。或触之以荤杂，或舍之以狐疑，致使获功，三分惟一。譬如衣服，裁截虽了，竟久不缝，必欲人着在身，未审从何惯带。盖缘有初无末，是以功绩不成。若人造诸经像，设诸斋食，及施与法师物等，一分失落，隐没差殊。其福不成，何劳须造，徒费财货，枉用初功，见在招殃，亡人获罪。即使重沦地狱，再受冥愆，苦楚切身，饥寒备体，逡巡不过，竟久难存，铁杖穿心，火丸燋口，重重须受，一一皆偿。走避无门，窜身何地，殃延七祖，祸及九玄，莫议逍遥，空思善果，纵生追悔，难免前非。都缘积善善锺，积恶恶至。如形逐影，似响随声，曲直难逃，清浊难显。汝等男女，精虔舍施，斋洁忏扬，莫假声名，便言功德。声名功德，其罪如斯。善女信男，悉皆谛受，即使魂灵解脱，灾厄蠲除。稽首归依，今当敬礼。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">人各恭敬</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，宗师度脱天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，法力超升天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄素揔德天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，太华应运天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，高皇玉帝天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，胜光宝耀天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，高胜玄景天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，中皇真际天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，元洞玉晨天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，高上太一天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，至极明空天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玉虚挺相天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，揔真元洞天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，流光应现天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，流鉴照幽天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，通远深渊天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，金容显相天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，垂形散影天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，流光四朗天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，洞元积感天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，高玄揔真天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，皓明梵宝天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，金光七素天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玉皇真极天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄上太微天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，洞惠虚容天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，明机流鉴天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，寻真大洞天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，凝神会圣天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄真太一天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，净满宝光天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大乘景德天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，耀明高上天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，普垂玄照天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玉润流耀天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，上华元一天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，七变元和天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，五劫开化天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，含光秘景天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，清都证极天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，灵化洞虚天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，冲虚入妙天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，延寿保命天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，北极明耀天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄虚太极天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，紫庭虚皇天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，金容应福天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，琼华十善天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄微隐耀天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">礼三宝已，复次忏悔</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">至心归命，十方常住三宝。臣等忏悔身业之罪，但缘分身贵贱，高下不均，禀气富贫，宽狭难等。或贵者深堂广宇，拖紫纡朱，银壁椒房，丹台玉楯。或贱者苍头跣足，逐后迎前，声气羁微，格式卑下。或富者轻裘肥马，奥室精餐，言语如锺，心襟似海。或贫者饥寒不迨，礼义何周，言行相违，施为无拟。纷兹身业，岂解秉持。违过更多，谁能坚守。前虽忏悔，尚恐未周。当此虔诚，咸希涤荡。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">天尊言曰，善哉如卿所言。护持世界，能为众生，方便问难，咨请诸疑，随意科数，一一具问，咸实希有，我今重叹不可思议，告诸众生，谛听修之，当为解脱。若有善男子、善女人等，父母终亡之后，资备生方，舍家资财，礼经乞忏，荡涤诸愆，获生天界。若有男女写经造像，设供持斋，财物有余，私将入己，其物直一钱已上，十年十倍，又复不还，身当重病，失其相好，鸟栖露宿，死弃人间，永无出期，纵得受生，聋盲六疾，恒不具足。若用旛花物者，七年七倍，又复不还，身当囚徒，在於囹圄，被着枷锁，絷系晨昏，劣瘦日深，告雪无已。若用布施法师物者，五年五倍，又复不还，身为贫穷，衣食寒贱，或为牛马，以力报酬，或作猪羊，以命相负，或幼遭孤孑，无处依投，倚傍他人，身口不备。都缘不孝，致此冤愆。汝等男女，咸皆谛受。勤修功德，秉戒身形，一心奉行，稽首而退。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">太上慈悲九幽拔罪忏卷之二竟</span>\r\n	</p>\r\n</p>', '1448325850', '0', '', '', '', '');
INSERT INTO `fu_curlture` VALUES ('3', '太上慈悲九幽拔罪忏卷之三', '<p style=\"font-family:宋体;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-family:宋体;\">\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">太上慈悲九幽拔罪忏卷之三</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">尔时，元始天尊为普救真人，演说东南方梵炁天君，碧霞境界无极之国，妙寂庄严。既竟，重为演说南方梵宝昌阳丹灵真老赤帝三炁天君，梵天境界，其有真人，名曰真老无极之国，端化庄严。若人临终之日，愿生彼国，当洁己清斋，请师奉礼，南方应号天尊，长跪忏悔，免恒沙罪障，获无量善因，炼质澄神，登真悟道。众等至心，今当敬礼。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">人各恭敬</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，南方玄真万福天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，高真大宝天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄和大度天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，紫宸降福天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，上惠弘慈天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，冥寂幽源天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，云光正德天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，还淳示像天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，安神寂照天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，紫极元君天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄真妙宝天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，四达无碍天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，六辩虚凝天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，明耀景秀天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，圆光炳耀天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，金映流真天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄上紫虚天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，太微妙极天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，灵光空相天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，无上景皇天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，虚明洞惠天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，宝玄流化天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，澄神宝劫天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，妙智堪常天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，五灵玄德天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，六度凝解天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，高元上相天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，普光弘济天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，凝玄高上天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，耀景元和天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，弘明宝相天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，惠空无滞天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，冲和远畅天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，含光万善天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，游虚常乐天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，端严上德天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，明霞通智天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，乘真道化天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，灵化妙见天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，莹德明皇天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，通玄释滞天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，德充无上天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，虚皇演圣天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄冥广运天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，慈忍救度天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，中极九老天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，元灵高圣天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，中皇太一天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，梵气弥纶天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，黄庭大保天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">礼三宝已，复次忏悔</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">至心归命，十方常住三宝。臣等忏悔心业之罪，伏自初分南北，渐辩东西，爱好嫌粗，喜富嗔贱，信心所作，不度短长，贪利忘恩，隔亲怜外。见华饰则无厌无足，闻简实则如隙如雠。罪积丘山，功无尘露。虑标名北部，夺算南宫，是敢忏悔道前，乞垂原赦。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">天尊言，若有善男子、善女人，从生至死，毁坏尊像，损破道场。或剥脱行人及出家法身。或破诸禁戒，啖荤血之徒，五逆生心，合舌为本，诽谤三宝，嗔怒慈亲。或弃贱憎贫，背夫逐妇。或造私润己，背正入邪，同炁不亲，交游益重。或用三宝物，无有愧心，贷债弥多，抵赖不少。或杀生偷盗，不信公途，向暗背明，欺存誷殁。或骂詈良善，凌突贤人，淫欲恒多，阴毒不息。诸如此罪，尽系冥关，不有忏扬，将何消荡。若有善男子、善女人，常行孝心，旦暮不怠。或为父母，或为九玄，或为交亲，或为血属，或为兄弟，或为己身，舍施资财，请师礼忏，精羞供养，严洁道场。或八节三元、五腊十直、七元本命、岁旦庚申，是此良辰，或持斋戒，可除八难，能去千灾，一切冤家，成恩洗雪。先亡离苦，久远生天。地狱停酸，幽关水闭。愚痴晓了，六疾痊平，涉历四时，不罹灾衅。汝等男女，各自慎行，兢兢在心，勿生厌倦，即逢善果，遇此因缘，忏悔既终，斋心敬礼。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">人各恭敬</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，三素洞神天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大哀愍苦天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，霞光朗耀天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，中玄正一天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，黄房洞晏天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄上虚无天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄微大宝天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，黄庭九老天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，威灵拔度天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大明皓气天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄真上景天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，太皇胜妙天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，灵元宝命天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，流光通道天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，飞华庆善天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，定相延和天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，回元至极天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，应飞光像天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，无心妙体天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，六度真容天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，兴龙道相天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，高真观妙天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，流变延真天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，妙乐难胜天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，元皇一炁天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄虚太素天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，真老福庆天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，青真妙觉天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，元精始老天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄和净德天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，太空惠量天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，明真四极天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，自然灵应天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，积庆天和天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，广运慈善天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，万灵导护天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，弘慈广济天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，明皇救善天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，开度福道天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，垂光慈荫天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，妙智善权天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，六合通明天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，开度忧危天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，升真大帝天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，神章八会天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，太丹三素天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，元和宝相天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，太真高上天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，流光万变天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，通玄真定天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼、大慈大悲救苦真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">礼三宝已，复次忏悔</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">至心归命，十方常住三宝。臣等忏</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">悔心业之罪，但以智见浅短，气格顽愚，恣纵而行，言执己是。贪婪无足，鄙吝何穷，见善而雠，闻恶可及。积财千万，犹说困悬，为罪深多，恒称公当。从初至末，岂觉岂知，积日积时，逾年逾月，不申忏洗，何以祛除。今对道前，咸希涤荡。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">天尊言，善男子、善女人，一切业缘，皆有报应。汝等谛受，次第具陈：铁床坏体，铜柱焦筋，以报邪淫之罪；寒冰雪山，痛心伤骨，以报劫盗之罪；拔舌吞火，灌口镕铜，以报两舌之罪；热灰埋体，猛火烧身，以报杀生之罪；铁犂耕背，恶乌啖睛，剑树分张，碓磨如面，以报邪眉怒眼之罪；身为饿鬼，循历镬汤，烧铁为丸，饥则当食，以报破斋违戒之罪；眉发堕落，遍体生疮，举身烘烂，以报破坏圣像道场之罪；口中出火，眼目生疮，遍体流脓，藏身无地，以报走脱出家人之罪；舌不得语，闭塞喉中，盲目亡睛，口唇横裂，以报谤说出家人之罪；痴聋小短，生死不分，或作夫妻，中道离别，以报疏隔父母之罪；或为牛马猪羊、鸟兽鹰犬，以报违负贷债之罪；或为仆使，寒贱切身，少寡幼孤，羁赢劣病，以报轻薄弟兄同气之罪。如斯报应，皆在目前。善女信男，咸自思想，虔心谛受，永断前愆，忏悔既终，奉行而退。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">太上慈悲九幽拔罪忏卷之三竟</span>\r\n	</p>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '1448325878', '0', '', '', '', '');
INSERT INTO `fu_curlture` VALUES ('4', '太上慈悲九幽拔罪忏卷之四', '<p style=\"font-family:宋体;\">\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">太上慈悲九幽拔罪忏卷之四</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">尔时，元始天尊为普救真人，演说南方梵宝昌阳丹灵真老赤帝三炁天君、梵天境界、无极之国，端化庄严。既竟，重为演说西南方梵炁天君，耀真境界，其有真人，名曰琼光无极之国，善救庄严。若人临终之日，愿生彼国，当洁己清斋，请师奉礼西南方应号天尊，长跪忏悔，免恒沙罪障，获无量善因，炼质澄神，登真悟道。众等至心，今当敬礼。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">人各恭敬</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，西南方太灵虚皇天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，中和善寂天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，崇爱延光天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大明焕照天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，普救群生天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，景德丹华天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，通光普照天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄机妙应天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，七元信惠天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，端相开妙天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，七曜延灵天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，混成开化天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，通明四洞天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，启幽无极天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，空玄明梵天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，太上隐真天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，灵都集庆天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，太灵普被天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，浮空七耀天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，云光四照天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，保命护魂天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，洞空圆极天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，法轮开化天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，丹华皓映天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，流明大帝天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄圃琼轮天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，留神远照天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，紫庭会圣天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，匠道众真天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，广运玄化天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，灵源普润天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，崇真演教天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，安化太无天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，耀景栖真天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，保光洞朗天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，八景回晨天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，圆通妙化天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大明至极天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，灵源广洽天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，含光朗耀天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲救苦真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，大慈大悲大惠真人。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">礼三宝已；复次忏悔</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">至心归命，十方常住三宝。臣等忏悔口业，无量之罪。或曾欺贤誷圣，毁道憎亲，谈讟他非，矜夸己是，呵风骂雨，恶暑厌寒，翻有为无，将粗作细，斗合良善，扬举愚痴，从口而生，为罪之本。诸多过咎，无量无边。今日向十方天尊前，洗心丹祷，归命忏悔，咸乞荡除。天尊言，若有善男子、善女人，一为天地，二为帝王，三为父母，四为众生，五为血属，六为己身。爰从受生已来，至于今日，自一岁一十、二十三十、四十五十，乃至百岁、百二十岁，必欲预修，还从一七、二七、三七、四七乃至七七、百日、周年，周而复始，乃至永年，所造功德，还依次第而作，第一造像，第二写经，次即造四十九尺长旛，设四十九人斋，安置道场，请诸尊像，并及法师，香火知识，燃四十九灯，照耀诸天，其灯为三层台，下阔上敛，圆转状如车轮，香华供养，随力丰俭，三日三夜，或七日七夜，倾心丹祷，忏悔虔祈。对三宝称扬，为先亡首过，言词惟在朴实，不得浮华，务彼精严，必彰福利。其次广赎生命，或百或千，随其多寡，亦不可得。其次伤悯贫穷下贱，不具足者，旅困羁栖者，病人孤老者，囚徒饥馁者，边鄙愚顽，不晓东西。无分善恶者，咸堪救度，使沐良缘。或值他方远客，有急依投，而为开度，济其厄难，救彼孤寒，悉令安乐，此亦荐导亡灵，其福最大。其次为先亡父母、亿劫种亲，写造三十六部尊经，任意多少，或一部、十部、百部乃至千部，随力多少，拔度魂神，其福无量。使见存利益、过往逍遥，汝等男女，悉当谛听，宜各奉行。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">人各恭敬</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，弘真度物天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，揔御众仙天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，法明灵鉴天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，太霄耀景天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，结朗重明天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，启真玄素天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，混化变生天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，思玄会寂天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，灵光皓映天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，华光流照天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，法音演畅天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，凝虚善寂天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，梵元真一天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，为宝积德天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，福感灵照天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，慈悲惠照天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，无量相好天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，光显开度天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，正觉凝耀天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，法轮运转天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，普照洞虚天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，广济群有天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，内观真极天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄宗众宝天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，虚无正极天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，智光普耀天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，法定通微天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，宝轮常运天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄真会寂天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄妙善权天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，等观真常天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，重光宝际天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，合和安忍天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，太玄灵耀天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，崇灵道惠天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，玄觉宝光天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，普慈护国天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，神光广被天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，方便善救天尊。</span>\r\n	</p>\r\n	<p style=\"font-family:宋体;\">\r\n		<span style=\"font-size:18px;\">志心朝礼，无为应物天尊。</spa