INSERT INTO `fu_curlture` VALUES ('19', '灶 王 经', '<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">步虚&nbsp;&nbsp;东厨司命主&nbsp;&nbsp;赐福消灾尊</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">升天朝上帝&nbsp;&nbsp;下界化从生</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">香厨妙供天尊</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">大道为公无上神&nbsp;&nbsp;依期上奏本天尊</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">晨昏叩首舒黎愿&nbsp;&nbsp;庚申虔诚体众生</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">桂枝香飘欣寿永&nbsp;&nbsp;彩烛辉煌塔圣思</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">年年庆祝邀神会&nbsp;&nbsp;国泰民安古代兴</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">香供养香厨妙供天尊</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">东厨司命号元皇&nbsp;&nbsp;庚申甲子奏上苍天</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">每日灶前多积污&nbsp;&nbsp;今朝祈祷保安康</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">道场众等&nbsp;&nbsp;人各恭敬&nbsp;&nbsp;恭对道前&nbsp;&nbsp;诵经如法</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">统乎五行达神明&nbsp;&nbsp;观乃二气为司命</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">掌管职禄与贫富&nbsp;&nbsp;稽查善恶奏天庭</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">净心神咒</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">太上台星&nbsp;应变无停&nbsp;&nbsp;驱邪缚魅&nbsp;&nbsp;保命护身</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">智慧明净&nbsp;心神安宁&nbsp;&nbsp;三魂永久&nbsp;&nbsp;魄无丧倾</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">净口神咒</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">丹朱口神&nbsp;吐积除氛&nbsp;&nbsp;舌神正伦&nbsp;&nbsp;通命养神</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">罗千齿神&nbsp;却邪卫真&nbsp;&nbsp;思神练液&nbsp;&nbsp;道气常存</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">净身神咒</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">灵宝天尊&nbsp;安慰身形&nbsp;&nbsp;弟子魂魄&nbsp;&nbsp;五藏玄冥</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">青龙白虎&nbsp;队仗纷纭&nbsp;&nbsp;朱雀玄武&nbsp;&nbsp;待卫身形</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">土地神咒</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">元始安镇&nbsp;普告万灵&nbsp;&nbsp;狱渎真官&nbsp;&nbsp;土地祈灵</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">左社右积&nbsp;不得妄&nbsp;&nbsp;&nbsp;&nbsp;回向正道&nbsp;&nbsp;内外肃清</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">各安方位&nbsp;备守帝庭&nbsp;&nbsp;太上有命&nbsp;&nbsp;搜捕邪精</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">护法神王&nbsp;保卫诵经&nbsp;&nbsp;皈依大道&nbsp;&nbsp;无亨利贞</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">天地神咒</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">天地自然&nbsp;积气氛散&nbsp;&nbsp;洞中玄虚&nbsp;&nbsp;晃良太元</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">八方威神&nbsp;使我自然&nbsp;&nbsp;灵宝符命&nbsp;&nbsp;普告九天&nbsp;</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">乾罗恒那&nbsp;洞罡太玄&nbsp;&nbsp;斩妖缚邪&nbsp;&nbsp;杀鬼万千</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">中山神咒&nbsp;元始玉文&nbsp;&nbsp;持诵一遍&nbsp;&nbsp;却病延年</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">按行五狱&nbsp;入海如闻&nbsp;&nbsp;魔王束首&nbsp;&nbsp;待卫称身</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">凶积消散&nbsp;道气常存</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">祝香咒</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">道由心学&nbsp;心假香假&nbsp;&nbsp;香热玉炉&nbsp;&nbsp;心存帝前</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">真灵正盼&nbsp;仙旗临轩&nbsp;&nbsp;令臣关告&nbsp;&nbsp;迳达九天</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">金光神咒</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">天地玄宗&nbsp;万气本根&nbsp;&nbsp;广修浩劫&nbsp;&nbsp;证吾神通</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">三界内外&nbsp;惟道独尊&nbsp;&nbsp;体有金光&nbsp;&nbsp;覆映吾身&nbsp;</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">视之不见&nbsp;听之不闻&nbsp;&nbsp;包罗天地&nbsp;&nbsp;养育群生</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">受持万遍&nbsp;身有光明&nbsp;&nbsp;三界待卫&nbsp;&nbsp;五帝司迎</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">万神朝礼&nbsp;役使雷霆&nbsp;&nbsp;鬼妖丧胆&nbsp;&nbsp;精怪忘形</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">内有霹雳&nbsp;雷神隐名&nbsp;&nbsp;洞慧交辙&nbsp;&nbsp;五气腾腾</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">金光速现&nbsp;覆护真人&nbsp;&nbsp;云篆太虚&nbsp;&nbsp;浩劫之初</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">乍遐乍迩&nbsp;或沉或浮&nbsp;&nbsp;五方徘徊&nbsp;&nbsp;一丈之余</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">天真皇人&nbsp;按笔乃书&nbsp;&nbsp;以演洞章&nbsp;&nbsp;次书灵符</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">元始下降&nbsp;真文诞敷&nbsp;&nbsp;昭昭其有&nbsp;&nbsp;冥冥其无&nbsp;</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">沉疴能自痊&nbsp;&nbsp;鹿劳溺可扶&nbsp;</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">幽冥将有赖&nbsp;&nbsp;由是升仙都</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">道言&nbsp;昔登昆仑之山。有一老姆。独处其中。莫知其由。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">是时即有妙行真人。上白天尊曰。此之老姆未审复是。可人独住此山。殊无畏惧。天尊曰惟此老母。是名种火之母。能上通天界。下统五行。达于神明。观乎二气。在天则为天帝。在人间乃为司命。又为北斗七元使者。主人寿命长短。富贵贫贱。掌人职禄。又为五帝灶君。管人住宅。十二时晨。普知人间之事。每月朔日。记人造诸善恶。及其功德。录其轻重夜半奏上天曹。定其簿书悉是此母也。凡人家灶皆有禁忌。若不忌之。此母能致祸殃。弗可免也。妙行真人曰。凡人家灶。未审有何忌讳。敢问斯言。幸垂教示。臣见众生。安知其趣。愿将告诸。浊世男女。普令忌之。得谐安稳。天尊曰。子可谛受。凡人家灶。不可以鸡毛犬骨。头发刀斧。不净柴薪。秽污等物。触犯灶神。即致男女水安。经求无利。疾患疮疤。癫狂瘴疫。眼目昏昧。梦想颠倒。因蚕不收。六玄虚耗。令客鬼无惧。乱入宅中。鸡犬作怪。盗贼侵欺。口舌妄起。男女邪迷。官府禁锢。家业流亡。皆为触犯所致。若有此难。急家谢之。可诸东方青帝灶君。南方赤帝。灶君。西方白帝灶君。北方黑帝灶君。中央黄帝灶君。五方五帝灶君夫人天厨灵灶。地厨神灶。天帝娇男娇女。囱中童子。童男童女。曾灶祖灶。灶公灶母。灶夫灶妇。灶子孙。家灶外灶。大灶小灶。长灶短灶。新灶旧灶。五方游奕灶君。左右将军。炊涛神女。前后直符灶君。小使进火神母。游火童子。灶家娘子。娣妹新妇。七十二灶。侍从神众。弟子某夫妻。眷属大小。并是凡夫。不知禁忌。触犯者多。不自省悟或作东西。或修南北。将刀向灶。惊动尊神。酒后高声。妄言绮变速器。以中进火。露体赤身。男女秽污。烹宰腥&nbsp;&nbsp;&nbsp;。歌吟厨内。叫唤灶前。致使三灾竞起。百怪惧生。财物耗散。疾病连绵。田蚕不利。仁宦不迁。买卖无益。宅舍衰遭恶人谋害。六畜不孽。梦想颠倒。所求不遂。即当虔诚。愿陈忏悔。天尊曰能如是念。祸必驱之。吾当为汝。末世众生召请此母大降灵通赦过宥罪致福受庆人口康泰。永风无殃。青龙郁郁。白虎锵锵。朱雀赫赫。玄武堂堂。冤家伏匿。盗贼潜藏。火光入地。百怪消洋。于是妙行真人。闻天尊说。此老母是名天帝司命。能掌人间罪福之事。普示群生。而作颂曰</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">天地临位&nbsp;司命灶君&nbsp;&nbsp;世人恭敬&nbsp;&nbsp;福禄咸臻</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">去除灾难&nbsp;疫疠潜奔&nbsp;&nbsp;仰依诲示&nbsp;&nbsp;普得安宁</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">天尊再告真人曰。末世男女。冲突灶君。有灾有患。皆须清净扫洒。明灯烧香。请道士转经。呼召灶君。眷属名字。奏献钱财。或供饮食。为人所。利子之所。受深宜谛焉。妙行真人乃稽首听命信受奉行</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">志心皈命礼&nbsp;&nbsp;位司喉舌&nbsp;&nbsp;径离保厨&nbsp;&nbsp;录人间功过之因</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">&nbsp;依期上奏&nbsp;&nbsp;体天地生成之化孚佑下民&nbsp;&nbsp;驱不正之鬼神&nbsp;</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">扫尘中之叱利&nbsp;一家吉庆&nbsp;合宅昌荣&nbsp;朝夕承匡扶之人&nbsp;</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">庚申言事非之日&nbsp;&nbsp;雍雍瑞相&nbsp;&nbsp;赫赫灵气&nbsp;大悲大愿&nbsp;&nbsp;大圣</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">大慈&nbsp;九天东厨司命&nbsp;&nbsp;元皇定福奏善天尊&nbsp;九天云厨&nbsp;斋灵</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">神云&nbsp;烟火部显威灵&nbsp;弘恩寡众生&nbsp;&nbsp;感格千真&nbsp;赐福保安宁</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">愿以此功德&nbsp;普及于一切&nbsp;赐福消灾障&nbsp;&nbsp;诵经保平安</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">经坛土地&nbsp;神之最灵&nbsp;升天达地&nbsp;&nbsp;出幽入冥&nbsp;为吾关奏&nbsp;&nbsp;不得留停&nbsp;&nbsp;有功之日&nbsp;&nbsp;名书上清&nbsp;&nbsp;向来诵经功德&nbsp;&nbsp;上奉高真&nbsp;&nbsp;下保平安&nbsp;赐福消灾&nbsp;&nbsp;同赖善功&nbsp;&nbsp;证无上道&nbsp;&nbsp;一切信礼&nbsp;志心称念&nbsp;&nbsp;元皇奏善天尊&nbsp;&nbsp;不可思议功德</span>', '1448432825', '1', '', '', '', '');
INSERT INTO `fu_curlture` VALUES ('20', '朝 礼 科 仪', '<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">说文：高兴寄远，陶然于自得之境，仙真可格，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">霄汉通帝居之乡；有闻灵音，咫尺别尘凡之嚣俗；</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">虚像玄华，恢宏并色空之渊澄；丹凤绣锦，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">玄霞映空，烟散南篆是谓福，玉龙乘风，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">醴泉涌金，鳞结北文而献寿。臣等身托鸿蒙，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">幸资夙庆，窃不自揆，周祝圣寿</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">（令）“守靖玉女，四明功曹，今欲朝礼，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">愿通达上闻”（击豉功拈香）</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">太上玄元，五灵五老君，四值功曹，龙虎使者，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">三气正神。今***弟子***稽首皈身、皈神、皈命。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">太清玄元无极大道，太上老君，太上丈人君，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">天帝君，天帝丈人九气仙都君，九气丈人君。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">百仟万种道气，一仟二佰灵官，太清御陛下。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">臣今得奉道真，辄希圆明，誓已立功修德。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">共保之吉，生成之惠。今逢良辰之吉，结界设坛，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">所具仪式，谨遵道范，恭行如法。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">众唱：稽首皈依天中尊，妙相光明如月轮；</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;历观诸天持世界，普施慈惠救众生；</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">寂寂浩劫为主宰，十方玄范尽归宗；</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">臣今稽首皈命礼，志心称念玉皇尊。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">香供养：玄都万寿天尊。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">提纲：群仙朝上帝，元始降洪恩；</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;道妙两相扶，祝圣寿无边。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">伏以：黄冠羽服，神居云水之乡；宝篆仙经，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">诚答乾坤之德。臣谨炷真香，至恭上啟。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">三清道祖，四御上帝，万象森罗，二仪列宿，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">五灵七老，亿圣千真，十极高穹，群仙众职，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">玄门啟派，流演真师，皇坛有祷，无鞅先灵，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">经筵护法，主职威灵。城皇社令，传奏等神。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">悉仗真香，普同供养。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">今据中华人民共和国**省**地方居住。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">焚修弟子***，恭逢****大吉良旦。集众清流，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">恭趋宝座，持诵仙号，上祝圣寿。伏愿，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">三声高呼，祝南山之长寿。一炷清香，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">达北极之至尊，日辉月朗，圣寿齐天。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">臣诚惶诚恐，稽首顿首。所愿道运昌隆，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">华图有永，四方清吉，天下太平，时和岁稔，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">五谷丰登。六道四生，咸沾恩佑。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">再祈众等修真有份，进道无魔，檀越皈依，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">道侣芬芳，二六时中，常居清静。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">一切之中，均叨默念，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">志心称念：太平护国天尊，玄都万寿天尊，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">常清常静天尊，不可思议功德。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">念诰：志心皈命礼。宗坛立极，道统心传，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">清微灵宝及先天，道德混元兼正乙。真风动荡，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">飞身鞭驾于雷霆。慧目常明，顾盼提携于日月。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">口口流传无上道，堂堂高振大宗风，说法度人，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">下作十方之教主，代天宣化，广开四海之宗门，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">普济群生，咸依正道。大慈大悲，大圣大慈，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">玄坛啟教，传道宗师，流演万法教主。起乐洒净。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">说文：臣不揣凡愚，僭祝圣寿，流霄建节，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">云霞浮十色之幡。大辂鸣銮，日月耀五明之扇。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">臣等挹瑞彩於上古，依末光於中天，采一时之奇，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">列七真之供。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">恭对道前，俯拜致诚。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">起韵：众等齐举步虚声，祝国迎祥结虔诚；</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">当日表奏金阙下，今朝香爇玉炉前；</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">华图巩固山何壮，大道遐昌日月明；</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">万明瞻仰尧舜日，岁稔丰登乐太平。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">说文：臣闻，道由心学，心假香传，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">出清微者名解脱，产聚窟者号返生，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">盖功非在物而在德。是闻不以鼻而以心，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">若人间卉木之为馨，岂天上神仙之所欠，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">然礼以少为贵者，物取心则诚焉。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">恭对道前，用伸进献名香呈进。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">起韵：修设一炉香，帝道遐昌瑞，步虚彻云层，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">丹凤铺鳞锦，恳悃达衷情，臣等志心诵，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">愿得沐苍生。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">说文：臣闻，仙苑群芳，奇花万种，玄圃骞林，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">木秀蕊华，繁英吐而灵风永昼，朱紫艳而</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">道日春融。盖功非凡世之色春，宜俻至尊之玩赏，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">谨化尘俗于空中，暂列琼芳之席筵。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">恭对道前，用伸进献：鲜花呈进。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">起韵：太玄色有象，至道初无形，承通妙合气，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">依稀降万灵，繁英吐缘枫，朱蕊艳帝苑，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">鹤鸣太和清，鹿啣紫御瓶，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">臣等志心诵，愿得沐苍生。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">说文：臣闻，烛灯朱焰，阳燧之精，扬丹辉之璀璨，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">若玄宫之仙境，义极取阳，鉴长明于道力，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">遍十极而洞照。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">恭对道前，用伸进献：银灯呈进。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">起韵：稽首侍三清，精心感上玄，修奉灯烛焰，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">委质表丹诚，阳燧郁仪精，真一佈五云，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">霞彩结九瑞，洞章映八文，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">臣等志心诵，愿得沐苍生。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">说文：臣闻，玉泽仙姿，陈于神水，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">挹东井黄华之沼，体现金光，注西华流汩之池，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">身生秀茂，九龙荐瑞，为荣千古，载源厥美，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">溪衍何穷，分取余波，飞仙可讬。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">恭对道前，用伸进献：净水呈进。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">起韵：俯仰存太上，华景秀丹田，玉泽荆仙姿，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">同发东井源，流汩渭何报，余波衍茂荣，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">旋想坎离气，龙驾倏来迎，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">臣等志心诵，愿得沐苍生。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">说文：臣闻，乘舆巡狩，进紫金之交梨，仙果省职，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">上赤灵之火枣，东掇扶桑之椹，北收崆峒之瓜。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">遗想美存，效仪献颂，三千成实，赤足为多，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">七百汗枝，愿言兮赐，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">恭对道前，用伸进献：仙果呈进。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">起韵：缥缈入大乘，道德随日新，妙哉玄范圃，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">调和阴阳根，碧悬透碧玉，果熟丹色鲜，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">桑採扶桑地，瓜收崆峒天，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">臣等志心诵，愿得沐苍生。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">功曰：妙体本来至一，化身千百未为奇；</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">臣等志心稽首礼，至诚摄念朝上帝。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">（起乐，宫观当家及众道侣朝礼）</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">功白：天听惟明，帝心有鉴，长而不宰，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">与天地以为宗，仰之弥高，超形名而独立，</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">思全民物，泽被邦家，清众虔诚，皈依三宝。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">皈依道，断脱无边障，往升最上乘。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">皈依经，深明经藏礼，得神通莫测。</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">皈依师，授首玄妙智，迳入大罗天。</span><br />', '1448432850', '1', '', '', '', '');
INSERT INTO `fu_curlture` VALUES ('21', '接 驾 科 仪', '<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">念止&nbsp;太上灵宝天尊说禳灾度厄真经&nbsp;</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">小启请&nbsp;仰启三清境无上三境尊</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">仰启大罗天&nbsp;无上玄穹主</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">仰启三清界&nbsp;侍臣诸真众</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">小赞&nbsp;&nbsp;道筵海会&nbsp;&nbsp;郁罗萧台&nbsp;金童玉女两边排&nbsp;&nbsp;</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">香花满瑶坛&nbsp;赐福消灾&nbsp;&nbsp;众圣降驾来</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">说文&nbsp;&nbsp;香烟满法界&nbsp;万圣齐降临</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">俯伏金阙下&nbsp;礼拜表真诚</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">步虚&nbsp;&nbsp;大梵三天主&nbsp;虚皇五老尊</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;尚难窥窍妙&nbsp;岂复入冥玄</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">举天尊&nbsp;大罗三宝天尊</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">吊挂&nbsp;&nbsp;三清圣号广宣扬&nbsp;&nbsp;一句能消万劫殃</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;七宝林中朝上帝&nbsp;&nbsp;五明宫内礼虚皇</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;常乘白鹤游三界&nbsp;&nbsp;每驾青牛遍十方</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;众等志心皈命礼&nbsp;&nbsp;鸾歌风舞赴到场</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">香供养&nbsp;道经师宝天尊</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">说文&nbsp;恭闻&nbsp;宝座森严&nbsp;移玉殿金于法界&nbsp;琳琅振响</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">降琼轮羽盖于宵衢&nbsp;瞻龙与以来临&nbsp;望风辇而拥侍</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">青都严虎豹&nbsp;非凡悃以能通&nbsp;黄道拥龙鸾</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">惟志诚而可格&nbsp;香花灯烛&nbsp;结为悬日之光华</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">宝殿瑶坛&nbsp;权作弥罗之境界&nbsp;今则天开黄道</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">地发祥烟&nbsp;上帝鉴观于下土&nbsp;高真巡察于凡间</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">校勘神人之功过&nbsp;俯赐群迷之嘉祥&nbsp;清众齐集</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">各整朝班&nbsp;竭一诚而奉请&nbsp;迓万圣以来临</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">众等转步天阶&nbsp;恭迎帝驾</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">天尊板&nbsp;鹤驾来临天尊</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">说文&nbsp;&nbsp;香自诚心起&nbsp;烟从信里来</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;一诚通天界&nbsp;诸真下瑶阶</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">步虚&nbsp;&nbsp;太极分高厚&nbsp;轻清上属天</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;人能修至道&nbsp;身乃作真仙</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">举天尊&nbsp;大罗三宝天尊</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">吊挂&nbsp;&nbsp;稽首虔诚拜昊天&nbsp;昊天今日赴经筵</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;经筵里面金光现&nbsp;光现空中宝珠悬</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;悬碧落朝元始&nbsp;元始说法度众生</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">香供养&nbsp;金容玉像天尊</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">说文&nbsp;臣闻&nbsp;神不自降&nbsp;有感皆通&nbsp;圣不虚临</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">祈祷必应&nbsp;伏念众等&nbsp;生居下土&nbsp;长自中华</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">无一善可报于皇天&nbsp;恐造罪若丘山&nbsp;无一功可答于</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">下土&nbsp;恐沉堕于幽冥&nbsp;今则洗心涤虑&nbsp;修崇斋醮</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">恭迎帝驾&nbsp;伏望&nbsp;上帝临坛&nbsp;高真降位&nbsp;祥光瑞气</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">下照太虚&nbsp;玉辇金车&nbsp;上离霄汉&nbsp;香风飒飒</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">吹秽气而远去它方&nbsp;法雨萧萧&nbsp;洒宝座而嚣尘不起</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">金童前引&nbsp;玉女后随&nbsp;天人对对&nbsp;齐唱不老之歌</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">仙女双双&nbsp;俱奏长生之典&nbsp;鸾歌风舞</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">迎圣驾以来临&nbsp;羽众排班&nbsp;望真驭而下降</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">道众运诚&nbsp;同声赞扬</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">举天尊&nbsp;神水解秽天尊</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">提科&nbsp;&nbsp;钟鼓琳琅响彻天&nbsp;十方肃静得安然</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;天无氛秽朝元始&nbsp;地无妖尘降驾来</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">说文&nbsp;伏以&nbsp;帝驾降临&nbsp;神风静默&nbsp;岂容妖魔</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">敢而常存&nbsp;当资法水之神&nbsp;以解坛场之秽</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">八方扫荡&nbsp;五路澄辉&nbsp;使碧落谒九霄之正气</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">净瑶坛接三界之灵光&nbsp;邀上帝之鸾与</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">迎高真之鹤驾&nbsp;仰祈洪贶&nbsp;普福民生</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">坛有清静灵章&nbsp;众当持诵</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">众唱&nbsp;天地自然&nbsp;秽气氛散&nbsp;洞中玄虚&nbsp;云云</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">清静之水&nbsp;日月光盖中藏北斗&nbsp;内隐三台</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">神水一洒&nbsp;厌秽速开净水洒过&nbsp;祸去福来</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">说文&nbsp;原夫&nbsp;法天象地&nbsp;迎鸾崇建于瑶坛</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">旋斗立箕&nbsp;秉诚进表于帝阙&nbsp;仰启紫禁之肃场</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">先示黄道以开通&nbsp;诚可格天&nbsp;一念感通于上下</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">敬而侍帝&nbsp;十方洞达于神明</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">坛有开天符命&nbsp;谨当告下</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">众唱&nbsp;所有开天符命今当告&nbsp;右符告下&nbsp;云云</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">说文&nbsp;伏以&nbsp;侍立通明&nbsp;曾睹红云之一朵</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">驭神金阙&nbsp;仰瞻宝盖之三重&nbsp;将迎上帝以临轩</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">先凭真香而达信焚香有偈&nbsp;宝号宣扬</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">举天尊&nbsp;香云达信天尊</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">提科&nbsp;&nbsp;五明宫内步虚长&nbsp;斗转璇玑夜未殃</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;瑶望层霄鹤驾下&nbsp;金炉添炷降真香</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">祝香咒&nbsp;道由心学&nbsp;云云</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">威&nbsp;&nbsp;&nbsp;&nbsp;九天之上&nbsp;谓之大罗&nbsp;玉京金阙&nbsp;云层峨峨</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">灵&nbsp;&nbsp;&nbsp;&nbsp;中有天帝&nbsp;仁慈惠和&nbsp;至道无敌&nbsp;降伏众魔</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">咒&nbsp;&nbsp;&nbsp;&nbsp;天宝灵符&nbsp;玉律金科&nbsp;神仙亿万&nbsp;幢幡众多</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">闻者罪灭&nbsp;永出爱河臣等皈命&nbsp;与道合真</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">提纲&nbsp;恭对天颜&nbsp;请称职位</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">称职&nbsp;臣系太上&nbsp;道德教下&nbsp;云云</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">圣板&nbsp;炉焚真香&nbsp;虔诚上启</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">渺渺大罗天上皇皇金阙宫中&nbsp;虚无自然</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">万道之祖&nbsp;玉清圣境元始天尊&nbsp;妙无上帝玉几下</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">恭望大道天慈俯赐恩光&nbsp;证盟修奉</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">鹤驾鸾与&nbsp;来临法会</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">志心皈命礼&nbsp;玉清圣境&nbsp;清微天宫&nbsp;郁罗萧台之中</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">森罗净弘之上&nbsp;现有为之梵相&nbsp;具无极之神通</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">敷演真玄&nbsp;破无有色空之碍&nbsp;拯提趣类</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\">绝智愚高下之分&nbsp;妙用难规&nbsp;灵机罔测</span><br />\r\n<span style=\"font-family:宋体;font-size:20px;line-height:normal;\"